from datetime import datetimefrom elasticsearch_dsl import DocType, Double, Date, Nested, Boolean, \    analyzer, InnerObjectWrapper, Completion, Keyword, Text, Integerfrom elasticsearch_dsl.connections import connectionsconnections.create_connection(hosts=["localhost"])from http import clientfrom urllib import request,parseclass CompanyType(DocType):    #汇桔云公司类型    company_name = Text(analyzer="ik_max_word")    legal_person = Text()    registered_capital = Text()    telephone_number = Integer()    email = Keyword()    company_url = Keyword()    address = Text()    registration_time = Date()    company_state = Text()    latest_update_time = Date()    registration_number = Integer()    organization_code = Integer()    uniform_credit_code = Text()    company_type = Text(analyzer="ik_max_word")    taxpayer_number = Integer()    industry = Text(analyzer="ik_max_word")    business_term = Text()    approval_date = Date()    registration_authority = Text()    english_name = Text()    registered_address = Text()    operation_scope = Text(analyzer="ik_max_word")    class Meta:        index = "company"        doc_type = "info"if __name__ == "__main__":    CompanyType.init()